'use strict';

// Declare app level module which depends on views, and components
angular.module('myApp', [])


    .controller('View1Ctrl', function ($scope, $http) {
        $scope.venueList = new Array();
        $scope.mostRecentReview;
        $scope.getVenues = function () {
            var placeEntered = document.getElementById("txt_placeName").value;

            if (placeEntered != null && placeEntered != "") {
                document.getElementById('div_ReviewList').style.display = 'none';
                //This is the API that gives the list of venues based on the place and search query.
                var handler = $http.get("https://sandbox-healthservice.priaid.ch/issues/"+placeEntered+"/info?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..FxtPiLdOEtlSgKrd1LLLArJ9SrcKAxinlQ3PWOfyUCM&language=en-gb&format=json");
                handler.success(function (data) {


                            $scope.venueList[0] = {
                                "name": data.Description
                            };



                })
                handler.error(function (data) {
                    alert("There was some error processing your request. Please try after some time.");
                });
            }
        }


    });
